import React, {useState, useEffect} from 'react';
import './Home.css';
import Meals from './Meals';

function Home() {
    const [meals, setMeals] = useState(null);
    const [ingredients, setIngredients] = useState('');

    function getMealData() {
        fetch(
            `https://api.spoonacular.com/mealplanner/generate?apiKey=cb1c464d94f142c08b156c5beddade8b&timeFrame=day&targetCalories=${ingredients}`
        )
            .then((response) => response.json())
            .then((data) => {
            setMeals(data);
            })
            .catch(() => {
            console.log("error");
            });
    }

    function handleValue(e) {
        setIngredients(e.target.value);
    }

    return (
        <div className='container'>
            <div className='banner'>
                <div className='grid'>
                    <div className='title'>
                        <strong>
                            Find a Recipe
                        </strong>
                    </div>
                    <div className='search'>
                        <input
                        type="text"
                        placeholder="Ingredients"
                        className='input_box'
                        />
                    </div>
                </div>
            </div>

            <div>
                Look for recipes with the ingredients in your fridge!
                {meals && <Meals mealData={meals} />}
            </div>
        </div>
    );
}

export default Home;
